import React from "react";
import StatesOptions from "./Options";
import "./styles/Forms.css";

class Forms extends React.Component {
  render() {
    return (
      <form>
        <fieldset className="form">
          <label for="name">Nome:</label>
          <input type="text" id="name" maxLength="40" required />

          <label for="email">Email:</label>
          <input type="text" id="email" maxLength="50" required />

          <label for="cpf">CPF:</label>
          <input type="text" id="cpf" maxLength="11" required />

          <label for="address">Endere√ßo:</label>
          <input type="text" id="address" maxLength="100" required />

          <label for="city">Cidade:</label>
          <input type="text" id="city" maxLength="28" required />

          <label for="state">Estado:</label>
          <select id="state">
            <option>Selecione</option>
            <StatesOptions />
          </select>
        </fieldset>
      </form>
    );
  }
}

export default Forms;
